package bhuvana;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String country="Japan";
		switch(country)
		{
			case"Japan":
			System.out.println("Hahaa");
			break;
			case"India":
			System.out.println("Heehee");
			break;
			case"US":
			System.out.println("Huhuuu");
			break;
			default:
			System.out.println("UK");
		}
		
		
	}

}
